<?phpclass Stars{    /**     * Get all stars from Db     *     * @param null $db     * @return mixed     */    public static function getAllStars($db)    {        $stars = $db->query('SELECT * FROM stars')->fetchAll();        return $stars;    }    /**     * Get stars where book_id = id in table books     *     * @param $db     * @param $id     * @return mixed     */    public static function getStarsByBookId($db, $id)    {        $stars = $db->query('SELECT * FROM stars Where books_id =' . $id)            ->fetchAll(PDO::FETCH_ASSOC);        return $stars;    }    /**     * Delete stars book_id = id in table books     *     * @param $db     * @param $id     * @return mixed     */    public static function deleteStars($db, $id)    {        $queryDeleteStars = 'DELETE FROM stars WHERE books_id = :id';        $delete = $db->prepare($queryDeleteStars);        $delete->bindParam(':id', $id, PDO::PARAM_INT);        $delete->execute();        return true;    }}